import{_ as h,c as t,o as a,ag as i}from"./chunks/framework.B-XtCDNB.js";const m=JSON.parse('{"title":"DELTARUNE (từ nơi đồng xanh thơm hương lúa)","description":"","frontmatter":{},"headers":[],"relativePath":"deltarune/index.md","filePath":"deltarune/index.md","lastUpdated":1760171288000}'),e={name:"deltarune/index.md"};function c(l,n,o,r,u,g){return a(),t("div",null,[...n[0]||(n[0]=[i('<h1 id="deltarune-tu-noi-đong-xanh-thom-huong-lua" tabindex="-1">DELTARUNE (từ nơi đồng xanh thơm hương lúa) <a class="header-anchor" href="#deltarune-tu-noi-đong-xanh-thom-huong-lua" aria-label="Permalink to &quot;DELTARUNE (từ nơi đồng xanh thơm hương lúa)&quot;">​</a></h1><h2 id="ve-chung-minh" tabindex="-1">Về chúng mình <a class="header-anchor" href="#ve-chung-minh" aria-label="Permalink to &quot;Về chúng mình&quot;">​</a></h2><p>Có thể nói, đây là lần đầu tiên nhóm mình bắt đầu hoạt động Việt hóa/phiên dịch. Đây cũng là bản dịch đầu tay do chúng mình thực hiện</p><p>Tuy nhiên, dự án này có duy nhất mình đảm nhiệm, với nhiều lý do khá quan trọng</p><blockquote><p>Thông thường, với kịch bản phức tạp của Undertale, phải có ít nhất hai dịch giả Anh-Nhật để phát hành kịp thời. Tuy nhiên, 8-4 đã chọn cách chậm lại và chỉ chọn một người. Điều này có nghĩa là trò chơi sẽ mất nhiều thời gian hơn và tốn nhiều tiền hơn để bản địa hóa, nhưng cũng mang lại lợi ích:</p><ul><li>Bản dịch sẽ có một giọng điệu nhất quán</li><li>Nhóm có thể dành thời gian chau chuốt câu từ mà không cần vội vàng</li><li>Giảm thiểu những thay đổi phút chót</li></ul><p>(trích <a href="https://archive.org/details/undertale-legends_of_localization_3" target="_blank" rel="noreferrer">Legends of Localization Book 3: UNDERTALE</a>)</p></blockquote><p>Đúng, muốn đi nhanh thì đi một mình, muốn đi xa hãy đi cùng nhau. Nhưng rõ ràng, để nhiều người phiên dịch cùng một lúc thì sự bóp méo câu chuyện là điều không tránh khỏi</p><h2 id="tien-trinh-chuong-1" tabindex="-1">Tiến trình - Chương 1 <a class="header-anchor" href="#tien-trinh-chuong-1" aria-label="Permalink to &quot;Tiến trình - Chương 1&quot;">​</a></h2><p>Vì lý do cá nhân, mình không thể dịch full time. Có thể mình không phải là người dịch nhanh nhất, nhưng mình là (một trong) người dịch sáng suốt nhất.</p><p>Cảm ơn các bạn đã chờ đợi.</p><input type="checkbox" id="phase1"><label for="phase1">Dịch game (55%)</label><br><input type="checkbox" id="phase2" value="on"><label for="phase2">Playtest &amp; proofread</label>',14)])])}const p=h(e,[["render",c]]);export{m as __pageData,p as default};
